let link=document.querySelectorAll("a");for(let e=0;e<link.length;e++)link[e].addEventListener("click",function(e){e.preventDefault()});const smoothLinks=document.querySelectorAll('a[href^="#"]');for(let t of smoothLinks)t.addEventListener("click",function(){var e=t.getAttribute("href");document.querySelector(e).scrollIntoView({behavior:"smooth",block:"start"})});document.querySelector(".header-nav__btn").addEventListener("click",function(){document.querySelector(".header-nav__menu").classList.toggle("header-nav__menu--active")});let menuItems=document.querySelectorAll(".header-nav__menu-item");for(let e=0;e<menuItems.length;e++)menuItems[e].addEventListener("click",function(){document.querySelector(".header-nav__menu").classList.remove("header-nav__menu--active")});